document.addEventListener("DOMContentLoaded",function(){let t=document.getElementById("consultoriaForm");var e=document.getElementById("generateReportBtn");let o=document.getElementById("reportSection"),r=document.getElementById("reportContent");t.addEventListener("submit",function(e){e.preventDefault();e=new FormData(t);fetch("/admin/consultoria/saveClient",{method:"POST",body:e}).then(e=>e.json()).then(e=>{e.success?(alert(e.success),t.reset()):alert(e.error||"Erro ao salvar os dados.")}).catch(e=>{console.error("Erro ao salvar os dados:",e),alert("Erro ao salvar os dados.")})}),e.addEventListener("click",function(){var e=new FormData(t);fetch("/admin/consultoria/generateReport",{method:"POST",body:e}).then(e=>e.json()).then(e=>{e.success?(o.style.display="block",r.textContent=e.recomendacoes):alert(e.error||"Erro ao gerar o relatório.")}).catch(e=>{console.error("Erro ao gerar o relatório:",e),alert("Erro ao gerar o relatório.")})})});