class Fetch{constructor(e){this.baseURL=e}async get(e,t){return this.request(e,"GET",null,t)}async post(e,t,s={}){return this.request(e,"POST",t,s)}async put(e,t={},s={}){return this.request(e,"PUT",t,s)}async delete(e,t={}){return this.request(e,"DELETE",null,t)}async request(e,t,s=null,r={}){t={method:t,headers:r,body:s?JSON.stringify(s):null},r=await fetch(this.baseURL+decodeURI(e),t),s=await r.json();if(r.ok)return s;throw new Error(`Error ${r.status}: `+s.message)}}